CREATE TABLE PERSON (IDPERSON BIGINT AUTO_INCREMENT NOT NULL, ADDRESS LONGTEXT, DOCUMENT BIGINT, E_MAIL VARCHAR(255), LAST_NAME VARCHAR(255), NAME VARCHAR(255), PASSWORD VARCHAR(255), PHONE BIGINT, PROFESSION VARCHAR(255), ROL VARCHAR(255), SALARY BIGINT, USER VARCHAR(255), PRIMARY KEY (IDPERSON))
CREATE TABLE BENEFIT (IDBENEFIT BIGINT AUTO_INCREMENT NOT NULL, BENEFITDESCRIPTION LONGTEXT, BENEFITTYPE VARCHAR(255), BENEFITVALUE BIGINT, IDHOME BIGINT, PRIMARY KEY (IDBENEFIT))
CREATE TABLE HOME (IDHOME BIGINT AUTO_INCREMENT NOT NULL, CITY VARCHAR(255), CONTRACTTYPE VARCHAR(255), HOMEADDRESS LONGTEXT, HOMETYPE LONGTEXT, HOMEVALUE BIGINT, IDPERSON BIGINT, PRIMARY KEY (IDHOME))
ALTER TABLE BENEFIT ADD CONSTRAINT FK_BENEFIT_IDHOME FOREIGN KEY (IDHOME) REFERENCES HOME (IDHOME)
